import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';

const getStock = async () => {
    try {
        const res = await fetch("http://localhost:3001/stock", {
            method: "GET",
            headers: {
                "Content-Type": "application/json"
            }
        });

        const data = await res.json();

        if (res.status === 201) {
            console.log("Données du stock récupérées.");
            setStockData(data);
        } else {
            console.log("Quelque chose s'est mal passé. Veuillez réessayer.");
        }
    } catch (err) {
        console.log(err);
    }
};

export default function Stock() {

    useEffect(() => {
        getStock();
    }, []);

    const [stockData, setStockData] = useState([]);

    const getStock = async () => {
        try {
            const res = await fetch("http://localhost:3001/stock", {
                method: "GET",
                headers: {
                    "Content-Type": "application/json"
                }
            });

            const data = await res.json();

            if (res.status === 201) {
                console.log("Stock data retrieved.");
                setStockData(data);
            } else {
                console.log("Something went wrong. Please try again.");
            }
        } catch (err) {
            console.log(err);
        }
    }

    const deleteItem = async (id) => {
        const response = await fetch(`http://localhost:3001/deleteitem/${id}`, {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json"
            }
        });

        const deletedata = await response.json();
        console.log(deletedata);

        if (response.status === 422 || !deletedata) {
            console.log("Error");
        } else {
            console.log("Item deleted");
            getStock();
        }
    }

    return (
        <div className='container-fluid p-5'>
            <h1>Stock Inventory</h1>
            <div className='add_button'>
                <NavLink to="/additem" className='btn btn-primary fs-5'> + Add New Item</NavLink>
            </div>
            <div className="overflow-auto mt-3" style={{ maxHeight: "38rem" }}>
                <table className="table table-striped table-hover mt-3 fs-5">
                    <thead>
                        <tr className="tr_color">
                            <th scope="col"></th>
                            <th scope="col">Product Name</th>
                            <th scope="col">Quantity</th>
                            <th scope="col">Product Price</th>
                            {/* Add more table headers for other item properties if needed */}
                            <th scope="col">Update</th>
                            <th scope="col">Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            stockData.map((item, index) => (
                                <tr key={index}>
                                    <th scope="row">{index + 1}</th>
                                    <td>{item.ProductName}</td>
                                    <td>{item.ProductQuantity}</td>
                                    <td>{item.ProductPrice}</td>
                                    {/* Render other item properties in additional columns */}
                                    <td><NavLink to={`/updateitem/${item._id}`} className="btn btn-primary"><i className="fa-solid fa-pen-to-square"></i></NavLink></td>
                                    <td><button className="btn btn-danger" onClick={() => deleteItem(item._id)}><i className="fa-solid fa-trash"></i></button></td>
                                </tr>
                            ))
                        }
                    </tbody>
                </table>
            </div>
        </div>
    );
}
