import React, { useEffect, useState } from 'react'
import { NavLink } from 'react-router-dom'

export default function Products() {

    useEffect(() => {
        getProducts();
    }, [])

    const [productData, setProductData] = useState([]);



    const getProducts = async (e) => {

        try {
            const res = await fetch("http://localhost:3001/products", {
                method: "GET",
                headers: {
                    "Content-Type": "application/json"
                }
            });

            const data = await res.json();

            if (res.status === 201) {
                console.log("Data Retrieved.");
                setProductData(data);
            }
            else {
                console.log("Something went wrong. Please try again.");
            }
        } catch (err) {
            console.log(err);
        }
    }



    const deleteProduct = async (id) => {

        const response = await fetch(`http://localhost:3001/deleteproduct/${id}`, {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json"
            }
        });

        const deletedata = await response.json();
        console.log(deletedata);

        if (response.status === 422 || !deletedata) {
            console.log("Error");
        } else {
            console.log("Product deleted");
            getProducts();
        }

    }

    const searchProduct = async (e) => {
        e.preventDefault();
        const searchValue = e.target.elements.search.value; // Assurez-vous que e.target et e.target.elements existent
        if (!searchValue) {
            console.log("La valeur de recherche est vide");
            return;
        }
        try {
            const res = await fetch(`http://localhost:3001/products/search/${searchValue}`, {
                method: "GET",
                headers: {
                    "Content-Type": "application/json"
                }
            });
            const data = await res.json();
            if (res.status === 200) {
                console.log("Résultats de la recherche :", data);
                setProductData(data);
            } else {
                console.log("Aucun produit trouvé");
                setProductData([]);
            }
        } catch (err) {
            console.log(err);
        }
    }

   // Ajouter cette fonction dans le composant Products
const addToStock = async (id) => {
    try {
        const response = await fetch(`http://localhost:3001/addtostock/${id}`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            }
        });
        const data = await response.json();
        if (response.status === 201) {
            console.log("Product added to stock.");
            // Rafraîchir la liste des produits et du stock
            getProducts();
            getStock();
        } else {
            console.log("Failed to add product to stock.");
        }
    } catch (error) {
        console.log("Error:", error);
    }
};
const getStock = async () => {
    try {
        const res = await fetch("http://localhost:3001/stock", {
            method: "GET",
            headers: {
                "Content-Type": "application/json"
            }
        });

        const data = await res.json();

        if (res.status === 201) {
            console.log("Stock data retrieved.");
            setStockData(data);
        } else {
            console.log("Something went wrong. Please try again.");
        }
    } catch (err) {
        console.log(err);
    }
}





    return (
        <>
            <div className='container-fluid p-5'>
                <h1>All Products </h1>
                <div className='d-flex justify-content-end align-items-center'>

                    <form className="d-flex"  onSubmit={searchProduct}>
                    <input className="form-control me-2" type="search" name="search" placeholder="Search" aria-label="Search" />
              <button className="btn btn-primary fs-5 me-3" type="submit">Search</button>
            </form>
                    <div className='add_button'>
                        <NavLink to="/insertproduct" className='btn btn-primary fs-5'> + Add New Product</NavLink>
                    </div>
                </div>
                <div className="overflow-auto mt-3" style={{ maxHeight: "38rem" }}>
                    <table className="table table-striped table-hover mt-3 fs-5">
                        <thead>
                            <tr className="tr_color">
                                <th scope="col"></th>
                                <th scope="col">Product Name</th>
                                <th scope="col">Product Price</th>
                                <th scope="col">Product Barcode</th>
                                <th scope="col"> Quantity</th>
                                <th scope="col">Update</th>
                                <th scope="col">Delete</th>
                                <th scope="col">Add to Stock</th>
                            </tr>
                        </thead>
                        <tbody>

                            {
                                productData.map((element, id) => {
                                    return (
                                        <>
                                            <tr>
                                                <th scope="row">{id + 1}</th>
                                                <td>{element.ProductName}</td>
                                                <td>{element.ProductPrice}</td>
                                                <td>{element.ProductBarcode}</td>
                                                <td>{element.ProductQuantity}</td>

                                                <td><NavLink to={`/updateproduct/${element._id}`} className="btn btn-primary"><i className="fa-solid fa-pen-to-square"></i></NavLink></td>
                                                <td><button className="btn btn-danger" onClick={() => deleteProduct(element._id)}><i className="fa-solid fa-trash"></i></button></td>
                                                <td><button className="btn btn-success"onClick={() => addToStock(element._id)}><i className="fa-solid fa-cart-plus"title="Add to Stock" ></i></button></td>


                                            </tr>
                                        </>
                                    )
                                })
                            }

                        </tbody>
                    </table>
                </div>

            </div>

        </>
    )
}
