import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';

export default function Stock() {
    const [stockData, setStockData] = useState([]);
    const [productsData, setProductsData] = useState([]);
    const [selectedProduct, setSelectedProduct] = useState("");

    useEffect(() => {
        getStock();
        getProducts();
    }, []);

    const getStock = async () => {
        try {
            const res = await fetch("http://localhost:3001/stock", {
                method: "GET",
                headers: {
                    "Content-Type": "application/json"
                }
            });

            const data = await res.json();

            if (res.status === 200) {
                console.log("Stock data retrieved.");
                setStockData(data);
            } else {
                console.log("Something went wrong while retrieving stock data.");
            }
        } catch (err) {
            console.log(err);
        }
    };

    const getProducts = async () => {
        try {
            const res = await fetch("http://localhost:3001/products", {
                method: "GET",
                headers: {
                    "Content-Type": "application/json"
                }
            });

            const data = await res.json();

            if (res.status === 200) {
                console.log("Products data retrieved.");
                setProductsData(data);
            } else {
                console.log("Something went wrong while retrieving products data.");
            }
        } catch (err) {
            console.log(err);
        }
    };

    const handleAddToStock = async () => {
        try {
            const response = await fetch("http://localhost:3001/addItemToStock", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify({ productId: selectedProduct })
            });

            const data = await response.json();

            if (response.status === 201) {
                console.log("Product added to stock successfully.");
                getStock(); // Mettre à jour les données du stock après l'ajout
            } else {
                console.log("Failed to add product to stock.");
            }
        } catch (err) {
            console.log(err);
        }
    };

    return (
        <div className='container-fluid p-5'>
            <h1>Stock Inventory</h1>
            <div className='add_button'>
                <NavLink to="/additem" className='btn btn-primary fs-5'> + Add New Item</NavLink>
            </div>
            <div className="overflow-auto mt-3" style={{ maxHeight: "38rem" }}>
                <table className="table table-striped table-hover mt-3 fs-5">
                    <thead>
                        <tr className="tr_color">
                            <th scope="col"></th>
                            <th scope="col">Item Name</th>
                            <th scope="col">Quantity</th>
                            <th scope="col">Price</th>
                            <th scope="col">Update</th>
                            <th scope="col">Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        {stockData.map((item, index) => (
                            <tr key={index}>
                                <th scope="row">{index + 1}</th>
                                <td>{item.itemName}</td>
                                <td>{item.quantity}</td>
                                <td>{item.price}</td>
                                <td><NavLink to={`/updateitem/${item._id}`} className="btn btn-primary"><i className="fa-solid fa-pen-to-square"></i></NavLink></td>
                                <td><button className="btn btn-danger" onClick={() => deleteItem(item._id)}><i className="fa-solid fa-trash"></i></button></td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {/* Formulaire pour ajouter un produit existant au stock */}
            <div className="mt-3">
                <h2>Add Product to Stock</h2>
                <select value={selectedProduct} onChange={(e) => setSelectedProduct(e.target.value)} className="form-select">
                    <option value="">Select a Product</option>
                    {productsData.map((product) => (
                        <option key={product._id} value={product._id}>{product.ProductName}</option>
                    ))}
                </select>
                <button onClick={handleAddToStock} className="btn btn-primary mt-2">Add to Stock</button>
            </div>
        </div>
    );
}
