import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';

export default function Products() {
    const [searchTerm, setSearchTerm] = useState('');
    const [productData, setProductData] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);

    useEffect(() => {
        getProducts();
    }, []);

    const getProducts = async () => {
        try {
            const res = await fetch("http://localhost:3001/products", {
                method: "GET",
                headers: {
                    "Content-Type": "application/json"
                }
            });

            const data = await res.json();

            if (res.status === 200) {
                console.log("Data Retrieved.");
                setProductData(data);
                setFilteredProducts(data); // Initialise les produits filtrés avec toutes les données
            } else {
                console.log("Something went wrong. Please try again.");
            }
        } catch (err) {
            console.log(err);
        }
    }

    const deleteProduct = async (id) => {
        try {
            const response = await fetch(`http://localhost:3001/deleteproduct/${id}`, {
                method: "DELETE",
                headers: {
                    "Content-Type": "application/json"
                }
            });

            if (!response.ok) {
                throw new Error('Error deleting product');
            }

            console.log("Product deleted");
            getProducts();
        } catch (err) {
            console.log(err.message);
        }
    }

    // Fonction pour gérer les changements dans le champ de recherche
    const handleSearch = (e) => {
        setSearchTerm(e.target.value); // Mettre à jour l'état searchTerm avec la valeur saisie

        // Filtrer les produits en fonction de searchTerm
        const filtered = productData.filter(product =>
            product.ProductName.toLowerCase().includes(e.target.value.toLowerCase())
        );
        setFilteredProducts(filtered);
    }

    return (
        <>
            <div className='container-fluid p-5'>
                <h1>All Products </h1>
                <div className='d-flex justify-content-end align-items-center'>
                    <form className="d-flex" role="search">
                        <input
                            className="form-control me-2"
                            type="search"
                            placeholder="Search"
                            aria-label="Search"
                            value={searchTerm}
                            onChange={handleSearch}
                        />
                        <button className="btn btn-primary fs-5 me-3" type="submit">Search</button>
                    </form>
                    <div className='add_button'>
                        <NavLink to="/insertproduct" className='btn btn-primary fs-5'> + Add New Product</NavLink>
                    </div>
                </div>
                <div className="overflow-auto mt-3" style={{ maxHeight: "38rem" }}>
                    <table className="table table-striped table-hover mt-3 fs-5">
                        <thead>
                            <tr className="tr_color">
                                <th scope="col"></th>
                                <th scope="col">Product Name</th>
                                <th scope="col">Product Price</th>
                                <th scope="col">Product Barcode</th>
                                <th scope="col">Product Quantity</th>
                                <th scope="col">Update</th>
                                <th scope="col">Delete</th>
                                <th scope="col">Add to Stock</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredProducts.map((element, id) => (
                                <tr key={element._id}>
                                    <th scope="row">{id + 1}</th>
                                    <td>{element.ProductName}</td>
                                    <td>{element.ProductPrice}</td>
                                    <td>{element.ProductBarcode}</td>
                                    <td>{element.ProductQuantity}</td>
                                    <td><NavLink to={`/updateproduct/${element._id}`} className="btn btn-primary"><i className="fa-solid fa-pen-to-square"></i></NavLink></td>
                                    <td><button className="btn btn-danger" onClick={() => deleteProduct(element._id)}><i className="fa-solid fa-trash"></i></button></td>
                                    <td><button className="btn btn-success"><i className="fa-solid fa-cart-plus" ></i></button></td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </>
    );
}
