import React, { useState, useEffect } from 'react';

export default function AddItem() {
    const [products, setProducts] = useState([]);
    const [selectedProduct, setSelectedProduct] = useState('');
    
    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await fetch("http://localhost:3001/products");
            if (response.ok) {
                const data = await response.json();
                setProducts(data);
            } else {
                console.error("Failed to fetch products");
            }
        } catch (error) {
            console.error("Error fetching products:", error);
        }
    };

    const handleProductChange = (e) => {
        setSelectedProduct(e.target.value);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!selectedProduct) {
            alert("Please select a product");
            return;
        }

        try {
            const response = await fetch("http://localhost:3001/addItemToStock", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify({
                    productId: selectedProduct
                })
            });
            if (response.ok) {
                console.log("Product added to stock successfully");
                // You can navigate to another page or display a success message here
            } else {
                console.error("Failed to add product to stock");
            }
        } catch (error) {
            console.error("Error adding product to stock:", error);
        }
    };

    return (
        <div className="container">
            <h1>Add New Item to Stock</h1>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="productSelect" className="form-label">Select a Product:</label>
                    <select className="form-select" id="productSelect" value={selectedProduct} onChange={handleProductChange}>
                        <option value="">Select a product</option>
                        {products.map(product => (
                            <option key={product._id} value={product._id}>{product.ProductName}</option>
                        ))}
                    </select>
                </div>
                <button type="submit" className="btn btn-primary">Add to Stock</button>
            </form>
        </div>
    );
}
